import React, { useContext, useState } from "react";
import PropTypes from "prop-types";
import { JobContext } from "../../store/JobContent";

function Post_job(props) {
    const [jobs, setjobs] = useContext(JobContext);
  const [title, setTitle] = useState({});
  const [company, setCompany] = useState({});
  const [place, setPlace] = useState({});
  const onNewSubmitPost = (e) => {
    e.preventDefault();
    // console.log(e.target[0].value)
    if(e.target[0].value==="")
    {
        return ;
    }
    const latestId=jobs.length+1;
    setjobs(prevJobs=>[...prevJobs,{id:latestId,title:title,company:company,place}])

  };
  return (
    <>
      <form onSubmit={onNewSubmitPost}>
        <div className="form-group">
          <label htmlFor="title">Title</label>
          <input
            type="text"
            id="title"
            className="form-control"
            onChange={(e) => setTitle({ ...title, title: e.target.value })}
          />
        </div>
        <div className="form-group">
          <label htmlFor="company">Company</label>
          <input 
            type="text"
            id="company"
            className="form-control" 
            onChange={e => setCompany({ ...company, title: e.target.value })} 
        />
        </div>
        <div className="form-group">
          <label htmlFor="place">Place</label>
          <input 
            type="text" 
            id="place" 
            className="form-control" 
            onChange={e => setPlace({ ...place, title: e.target.value })} 
          />
        </div>
        <div className="form-group">
          <button className="btn btn-success">Post New Job</button>
        </div>
      </form>
    </>
  );
}

Post_job.propTypes = {};

export default Post_job;
