/* eslint-disable react/jsx-no-comment-textnodes */
import React, { useContext, useState } from "react";
import PropTypes from "prop-types";
import { JobContext } from "../store/JobContent";
import Job_list from "./jobs/job_list";
import { Route, useHistory } from "react-router";
import Post_job from "./jobs/post_job";
import Job_detail from "./jobs/job_detail";

function Jobs(props) {
  const [jobs, setjobs] = useContext(JobContext);
  const [showJobs, setShowJobs] = useState(true);
  const history = useHistory();

  const showJobList = () => {
    setShowJobs(!showJobs);
  };

  const postJob = () => {
    history.push("jobs/newpost")
  };

  const showJob= (e)=>{
    // console.log(e);
    history.push(`/jobs/${e}`);
  }
  const editJob= (e)=>{
    // console.log(e);
    history.push(`/jobs/${e}/edit`);
  }
  const nextPath=(path)=> {
    history.push(path);
  }
  return (
    <>
      <div className="container">
        <div className="row">
          <button className="btn btn-primary" onClick={showJobList}>
            job-list
          </button>
          <button className="btn btn-primary" onClick={() => nextPath('/jobs/new/post') }>
            job-post
          </button>
        </div>
      </div>
      <div className="container">
        <div className="row">
          <div
            className="col-xs-6 col-sm-6 float-left"
            style={{ marginTop: "50px" }}
          >
            {showJobs && (
              <table className="table table-striped">
                <thead>
                  <tr>
                    <th>id</th>
                    <th>title</th>
                    <th>company</th>
                    <th>place</th>
                    <th>time</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {jobs.map((job) => (
                    // eslint-disable-next-line react/jsx-pascal-case
                    <Job_list key={job.id} job={job} showJob={showJob} editJob={editJob}></Job_list>
                  ))}
                </tbody>
              </table>
            )}
          </div>
          <div className="col-xs-6 col-sm-6 float-right" style={{ marginTop: "50px" }}>
            <div className="">
              <Route path="/jobs/new/post" exact>                
                <Post_job />
              </Route>
              <Route path="/jobs/:jobId" exact>                
                <Job_detail/>
              </Route>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

Jobs.propTypes = {};

export default Jobs;
