import React, { useContext, useState } from "react";
import PropTypes from "prop-types";
import { JobContext } from "../../store/JobContent";
import { useParams } from "react-router";

function Edit_job(props) {
  const [jobs, setjobs] = useContext(JobContext);
  const [titleName, setTitle] = useState("");
  const [company, setCompany] = useState();
  const [place, setPlace] = useState();

  const params = useParams();
  const p1 = params.jobId;
  // console.log(p1);
  var rows = [];

  jobs.map((job) => (job.id == p1 ? rows.push(job) : ""));

  return (
    <>
    <h1>Edit Form</h1>
      <form onSubmit="">
        <div className="form-group">
          <label htmlFor="titleName">Title</label>
          <input
            type="text"
            id="titleName"
            className="form-control"
            value={rows[0].title}
          />
        </div>
        <div className="form-group">
          <label htmlFor="company">Company</label>
          <input
            type="text"
            id="company"
            className="form-control"
            value={rows[0].company}
          />
        </div>
        <div className="form-group">
          <label htmlFor="place">Place</label>
          <input
            type="text"
            id="place"
            className="form-control"
            value={rows[0].place}
            onChange={(e) => setPlace({ ...place, titleName: e.target.value })}
          />
        </div>
      </form>
    </>
  );
}

Edit_job.propTypes = {};

export default Edit_job;
