import React, { useContext } from "react";
import PropTypes from "prop-types";
import { JobContext } from "../store/JobContent";
import Job_list from "./jobs/job_list";

function Jobs(props) {
  const [jobs, setjobs] = useContext(JobContext);
  return (
    <>
      <button class="btn btn-primary">job-list</button>
      <button class="btn btn-primary float-right">job-post</button>
      <div class="container">
        <div class="row">
          <div class="col-xs-6 col-sm-6" style="margin-top: 50px;">
            <table class="table table-striped">
              <thead>
                <th>id</th>
                <th>title</th>
                <th>company</th>
                <th>place</th>
                <th>time</th>
              </thead>
              <tbody>
                {jobs.map((job) => (
                  <Job_list key={job.id} job={job}></Job_list>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </>
  );
}

Jobs.propTypes = {};

export default Jobs;
