import React, { useContext, useState } from "react";
import PropTypes from "prop-types";
import { JobContext } from "../store/JobContent";
import Job_list from "./jobs/job_list";
import { useHistory } from "react-router";

function Jobs(props) {
  const [jobs, setjobs] = useContext(JobContext);
  const [showJobs, setShowJobs] = useState(true);
  const history=useHistory();

  const showJobList= ()=>{
    setShowJobs(!showJobs);
  }

  const postJob= ()=>{
    setShowJobs(!showJobs);
  }
  return (
    <>
    <div className="container">
    <div className="row">
      <button className="btn btn-primary" onClick={showJobList}>job-list</button>
      <button className="btn btn-primary float-right" onClick={postJob}>job-post</button>
      </div>
      </div>
      <div className="container">
        <div className="row">

          <div className="col-xs-6 col-sm-6" style={{ marginTop: "50px" }}>
            {showJobs&&<table className="table table-striped">
              <thead>
                <tr>
                  <th>id</th>
                  <th>title</th>
                  <th>company</th>
                  <th>place</th>
                  <th>time</th>
                </tr>
              </thead>
              <tbody>
                {jobs.map((job) => (
                  // eslint-disable-next-line react/jsx-pascal-case
                  <Job_list key={job.id} job={job}></Job_list>
                ))}
              </tbody>
            </table>}
          </div>
        </div>
      </div>
    </>
  );
}

Jobs.propTypes = {};

export default Jobs;
