import PropTypes from 'prop-types'
import Jobs from '../components/jobs';

export const JobContext=createContext();

export const JobProvider= props=> {
    const [movies, setMovies] = useState([
        {
            id: 1,
            title: 'Adviser',
            company: 'TATA',
            place: 'Chennai',
            time: '01-11-21',
          },
          {
            id: 2,
            title: 'Accountant',
            company: 'Spacex',
            place: 'Florida',
            time: '11-11-21',
          },
          {
            id: 3,
            title: 'Webdeveloper',
            company: 'TCS',
            place: 'Mumbai',
            time: '21-11-21',
          }
    ])
    return (
        <div>
            <JobContext.Provider jobs={'hii'}>
                {props.children}
                {/* <Jobs /> */}
            </JobContext.Provider>
        </div>
    )
}
